//весь AuthContext служит нам только для функционала логина и регистрации
import axios from 'axios';
import { createContext } from 'react';
import { useEffect, useState } from 'react';
import { addRequest } from '../axios';
export const AuthContext = createContext();

export const AuthContextProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(JSON.parse(localStorage.getItem('user')) || null);

  const login = async (loginInput) => {
    //отправляем запрос на auth/login с объектом LoginInput из Login.tsx
    const res = await addRequest.post('/auth/login', loginInput, {
      withCredentials: true, //Указывает, что при отправке запроса в нем должны быть куки и заголовки авторизации
    });

    //нужно провервить что возварщает данный запрос(мое предположение, что токен)
    setCurrentUser(res.data); //берем данные из LS, чтобы передавать в currentUser и отдать их в компоненты на фронте
  };

  useEffect(() => {
    localStorage.setItem('user', JSON.stringify(currentUser)); //записываем данные в LS(указывается id, username, email, id, name, ava) для дальнейшей работы в Profile, Posts и тд(вытакиваем, чтобы узнать пользователя)
  }, [currentUser]); //обновляем при изменении currentUser

  return <AuthContext.Provider value={{ currentUser, login }}>{children}</AuthContext.Provider>;
};
