import React, { useContext, useState } from 'react';
import './comments.scss';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../context/authContext';
import Comment from '../comment/Comment';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { addRequest } from '../../axios';

const fetchComments = async (postId) => {
  const { data } = await addRequest.get(`comments?postId=${postId}`);
  return data;
  console.log(data.data);
};

const Comments = ({ postId }) => {
  const [desc, setDesc] = useState('');
  const currentUser = useContext(AuthContext);

  const { data, isError, isLoading } = useQuery(['comments'], () => fetchComments(postId));

  if (isLoading) {
    return <p>Loading....</p>;
  }

  if (isError) {
    return <p>Error for get comments</p>;
  }

  return (
    <div>
      <input value={desc} onChange={(e) => setDesc(e.target.value)}></input>
      <button>Add comment</button>
    </div>
  );
};
export default Comments;
