import axios from 'axios';
import { createContext } from 'react';
import { useEffect, useState } from 'react';

export const AuthContext = createContext();

export const AuthContextProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(JSON.parse(localStorage.getItem('user')) || null);

  const login = async () => {
    const data = await axios.post('http://localhost:9999/backend/auth/login', userData, {
      withCredentials: true,
    });

    setCurrentUser({
      id: 1,
      name: 'Vova Markelov',
      profilePic:
        'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
    });
  };

  useEffect(() => {
    localStorage.setItem('user', JSON.stringify(currentUser));
  }, [currentUser]);

  return <AuthContext.Provider value={{ currentUser, login }}>{children}</AuthContext.Provider>;
};
