import { useMutation, useQueryClient } from '@tanstack/react-query';
import { addRequest } from '../../axios';
import { AuthContext } from '../../context/authContext';
import './addPost.scss';
import React, { useContext, useState } from 'react';

const AddPost = () => {
  const { currentUser } = useContext(AuthContext);

  const queryClient = useQueryClient();
  const mutation = useMutation(
    (newPost) => {
      return addRequest.post('/post', newPost);
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['posts']);
      },
    },
  );

  const [img, setImg] = useState(null);
  const [desc, setDesc] = useState('');

  const handleChangeImg = async (e) => {
    try {
      const formData = new FormData();
      formData.append('image', img);
      console.log(formData);
      const res = await addRequest.post('/upload', formData);
      return res.data;
    } catch (error) {
      console.log(error);
      alert('Не удалось загрузить изображение');
    }
  };

  const submitPost = async (e) => {
    e.preventDefault();
    try {
      const params = { img, desc };
      let imgUrl = '';
      if (img) imgUrl = await handleChangeImg();
      mutation.mutate({ desc, img: imgUrl });
      setDesc('');
      setImg(null);
    } catch (error) {
      console.log(error);
      alert('Не удалось создать пост');
    }
  };

  return (
    <div className="addPost">
      <div className="container">
        <div className="top">
          <div className="left">
            <img src="" alt="" />
            <input
              onChange={(e) => setDesc(e.target.value)}
              type="text"
              placeholder="Enter text your post"
              value={desc}
            />
          </div>
          <div className="right">
            <img src="" alt="" />
          </div>
        </div>
        <hr />
        <div className="bottom">
          <div className="left">
            <input
              type="file"
              id="file"
              onChange={(e) => setImg(e.target.files[0])}
              style={{ display: 'none' }}
            />
            <label htmlFor="file">
              <div className="item">
                <img src="" alt="" />
                <span>Добавить изображение</span>
              </div>
            </label>
          </div>
          <div className="right">
            {img && (
              <>
                <img src={URL.createObjectURL(img)} />
                <button onClick={(e) => setImg(null)}>Удалить изображение</button>
              </>
            )}
            <button onClick={submitPost}>Add Post</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddPost;
