import './posts.scss';
import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import Post from '../post/Post';
import { QueryClient, QueryClientProvider, useQuery } from '@tanstack/react-query';

const Posts = () => {
  const { data, isLoading, isError } = useQuery(['posts'], fetchPosts);
  const fetchPosts = async () => {
    const res = await axios.get('http://localhost:9999/backend').then((res) => {
      return res.data;
    });
  };

  const posts = [
    {
      id: '1',
      name: 'Vova Markelov',
      userId: '1',
      profilePic:
        'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
      desc: 'Post 1 desc',
      img: 'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
    },
    {
      id: '2',
      name: 'Vova Markelov',
      userId: '2',
      profilePic:
        'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
      desc: 'Post 2 desc',
      img: 'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
    },
    {
      id: '3',
      name: 'Vova Markelov',
      userId: '3',
      profilePic:
        'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
      desc: 'Post 3 desc',
      img: 'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
    },
  ];

  return (
    <div className="posts">
      {posts.map((item) => (
        <Post post={item} key={item.id} />
      ))}
    </div>
  );
};
export default Posts;
