import React from 'react';
import './update.scss';

const Update = ({ setOpenUpdate }) => {
  const [coverPic, setCoverPic] = useState(null);

  const handleChange = (e) => {
    setText({ ...prev, [e.target.name]: [e.target.value] });
  };

  const upload = async () => {
    try {
      const formData = new FormData();
      const values = Object.fromEntries(formData.entries());
      formData.append();
    } catch (error) {
      alert('error to send data');
    }
  };

  return (
    <div className="update">
      Update
      <form>
        <label>Your CoverPicture</label>
        <input type="file" onChange={() => setCoverPic(e.target.files[0])} />
        <input type="file" />
        <input type="text" name="name" onChange={handleChange} />
        <input type="text" name="birthday" onChange={handleChange} />
        <input type="text" name="city" onChange={handleChange} />
        <input type="text" name="books" onChange={handleChange} />
        <button onClick={handleSubmit}>Update</button>
      </form>
      <button onClick={() => setOpenUpdate(false)}>X</button>
    </div>
  );
};

export default Update;
