import { addRequest } from '../../axios';
import { AuthContext } from '../../context/authContext';
import './addPost.scss';
import React, { useContext, useState } from 'react';

const AddPost = () => {
  const { currentUser } = useContext(AuthContext);

  const [img, setImg] = useState(null);
  const [desc, setDesc] = useState('');

  const handleChangeImg = async (e) => {
    try {
      const formData = new FormData();
      const file = e.target.files[0];
      formData.append('image', file);
      setImg(file);
      console.log(formData);
      const res = await addRequest('/upload', formData);
      return res.data;
    } catch (error) {
      console.log(error);
      alert('Не удалось загрузить изображение');
    }
  };

  const onSubmitPost = async (e) => {
    try {
      const params = { img, desc };
      const res = await addRequest('/posts', params);
      console.log(res.data);
      setDesc('');
      setImg(nul);
    } catch (error) {
      console.log(error);
      alert('Не удалось создать пост');
    }
  };

  return (
    <div className="addPost">
      <div className="container">
        <div className="top">
          <div className="left">
            <img src="" alt="" />
            <input
              onChange={(e) => setDesc(e.target.value)}
              type="text"
              placeholder="Enter text your post"
              value={desc}
            />
          </div>
          <div className="right">
            <img src="" alt="" />
          </div>
        </div>
        <hr />
        <div className="bottom">
          <div className="left">
            <input type="file" id="file" onChange={handleChangeImg} style={{ display: 'none' }} />
            <label htmlFor="file">
              <div className="item">
                <img src="" alt="" />
                <span>Добавить изображение</span>
              </div>
            </label>
          </div>
          <div className="right">
            {img && <img src={URL.createObjectURL(img)} />}
            <button onClick={onSubmitPost}>Add Post</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddPost;
