import React, { useContext } from 'react';
import './comments.scss';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../context/authContext';
import Comment from '../comment/Comment';
import { useQuery } from '@tanstack/react-query';
import { addRequest } from '../../axios';

const Comments = ({ postId }) => {
  const { currentUser } = useContext(AuthContext);
  const { isLoading, error, data } = useQuery(['comments'], () =>
    addRequest.get('/posts').then((res) => {
      return res.data;
    }),
  );

  const comments = [
    {
      id: '1',
      name: 'Vova Markelov',
      userId: '1',
      profilePic:
        'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
      desc: 'Post 1 desc',
      img: 'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
    },
    {
      id: '2',
      name: 'Vova Markelov',
      userId: '2',
      profilePic:
        'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
      desc: 'Post 2 desc',
      img: 'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
    },
    {
      id: '3',
      name: 'Vova Markelov',
      userId: '3',
      profilePic:
        'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
      desc: 'Post 3 desc',
      img: 'https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg',
    },
  ];
  return (
    <div className="comments">
      <div className="write">
        <img src={currentUser.profilePic} alt="" />
        <input type="text" placeholder="write your comment" />
        <button>Send</button>
      </div>
      {comments.map((comment) => (
        <Comment comment={comment} key={comment.id} />
      ))}
    </div>
  );
};
export default Comments;
