import './post.scss';
import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import ShareIcon from '@mui/icons-material/Share';
import { AuthContext } from '../../context/authContext';
import Comments from '../comments/Comments';

type PostProps = {
  id: string;
  userId: string;
  profilePic: string;
  desc: string;
  img: string;
  createdAt: string;
};

const Post: React.FC<PostProps> = ({ id, userId, profilePic, desc, img }) => {
  const { currentUser } = useContext(AuthContext);
  const [liked, setLiked] = useState<Boolean>(false);
  const [commentOpen, setCommentOpen] = useState(false);
  const navigate = useNavigate;

  return (
    <div className="post">
      <div className="container">
        <div className="user">
          <div className="userInfo">
            <img src={profilePic} alt="" />
            <div className="details">
              <Link className="links" to={`/profile/:${userId}`}>
                <span className="name">name</span>
              </Link>
              <span className="date">date</span>
            </div>
          </div>
        </div>
        <div className="content">
          <p>{desc}</p>
          <img src={'./upload' + img} alt="не открылось" />
        </div>
        <div className="info">
          <div className="item">
            <FavoriteBorderIcon className={liked ? 'active' : ''} />
            <span>1 likes</span>
          </div>
          <div onClick={() => setCommentOpen(!commentOpen)} className="item">
            <ChatBubbleOutlineIcon />
            Коментарии
          </div>
          <div className="item">
            <ShareIcon />
            Поделиться
          </div>
        </div>
        <div className="comments">{commentOpen && <Comments />}</div>
      </div>
    </div>
  );
};
export default Post;
