import React, { useContext, useState } from 'react';
import './comments.scss';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../context/authContext';
import Comment from '../comment/Comment';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { addRequest } from '../../axios';

const Comments = ({ postId }) => {
  const [desc, setDesc] = useState('');

  const { currentUser } = useContext(AuthContext);

  const { isLoading, error, data } = useQuery(['comments'], () =>
    addRequest.get('/comments?postId=' + postId).then((res) => {
      return res.data;
    }),
  );

  //ADD COMMENT
  const queryClient = useQueryClient();

  const mutation = useMutation(
    (newComment) => {
      addRequest.post('/comments', newComment).then((res) => {
        return res.data;
      });
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['comments']);
      },
    },
  );

  const handleClick = async (e) => {
    e.preventDefault();
    mutation.mutate({ desc, postId });
    setDesc('');
  };

  return (
    <div className="comments">
      <div className="write">
        <img src={currentUser.profilePic} alt="" />
        <input
          type="text"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
          placeholder="write your comment"
        />
        <button onClick={handleClick}>Send</button>
      </div>
      {error
        ? 'Something error in comments'
        : isLoading
        ? 'LOADING'
        : data.map((comment) => <Comment comment={comment} key={comment.id} />)}
    </div>
  );
};
export default Comments;
