import { useQuery } from '@tanstack/react-query';
import Posts from '../../components/posts/Posts';
import './profile.scss';
import React from 'react';
import { addRequest } from '../../axios';
import { useLocation } from 'react-router-dom';

const Profile: React.FC = () => {
  const userId = useLocation().pathname.slice(1);

  const { data, isLoading, isError } = useQuery(['data'], () =>
    addRequest.get(`/users/:${userId}`).then((res) => {
      return res.data;
    }),
  );

  return (
    <div className="profile">
      <div className="images">
        <img
          src="https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg"
          alt="Заставка"
          className="cover"
        />
        <img
          src="https://gamerwall.pro/uploads/posts/2022-04/1651059253_56-gamerwall-pro-p-ognennii-petukh-krasivie-oboi-69.jpg"
          alt="ава"
          className="profilePic"
        />
      </div>
      <div className="profileContainer">
        <div className="userInfo">
          <div className="top">
            <div className="left">
              <div className="item">Date of birthday:</div>
              <div className="item">He(her) Town:</div>
              <div className="item">I prepear this books:</div>
            </div>
            <div className="right">
              <div className="item">12 october 1999</div>
              <div className="item">Vologda</div>
              <div className="item">12 Oushan`s friends</div>
            </div>
          </div>
          <div className="buttons">
            <div className="buttons">
              <button className="add-button">Add to friend</button>
              <button className="delete-button">Don't look</button>
            </div>
          </div>
        </div>
      </div>
      <Posts />
    </div>
  );
};

export default Profile;
