import axios from 'axios';

export const addRequest = axios.create({
  baseURL: 'http://localhost:9999/backend',
  withCredentials: true,
});

export interface IRegisterPayload {
  username: string;
  email: string;
  password: string;
  name: string;
}

export interface ILoginPayload {
  username: string;
  password: string;
}

export const registerUser = async (registerData: IRegisterPayload) => {
  try {
    const data = await axios.post('http://localhost:9999/backend/auth/register', registerData);
  } catch (error) {
    console.log(error);
    alert('Не удалось зарегистрироваться, попробуйте снова');
  }
};

const loginUser = async (loginData: ILoginPayload) => {
  //отправляем запрос на auth/login с объектом LoginInput из Login.tsx
  const res = await addRequest.post('/auth/login', loginData, {
    withCredentials: true, //Указывает, что при отправке запроса в нем должны быть куки и заголовки авторизации
  });
  return res.data;
};
