import React, { useContext, useState } from 'react';
import './comments.scss';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../context/authContext';
import Comment from '../comment/Comment';
import { useQuery, useMutation, useQueryClient, isError } from '@tanstack/react-query';
import { addRequest } from '../../axios';

const Comments = ({ postId }) => {
  const [desc, setDesc] = useState('');
  const { currentUser } = useContext(AuthContext);

  const fetchComments = async () => {
    const { res } = await addRequest.get(`/comments/postId=${postId}`).then((res) => {
      return res.data;
    });
  };

  const createComment = async () => {
    const { res } = await addRequest.post('/comments', newComment).then((res) => {
      return res.data;
    });
  };

  const queryClient = useQueryClient();

  const { isLoading, isError, data } = useQuery(['comments'], () =>
    addRequest.get('/comments?postId=' + postId).then((res) => {
      return res.data;
    }),
  );

  const mutation = useMutation((newComment) => createComment(newComment), {
    onSuccess: () => {
      queryClient.invalidateQueries(['comments']);
    },
  });

  const handleClick = (e) => {
    e.preventDefault();
    mutation.mutate({ desc, postId });
    setDesc('');
  };

  return (
    <div className="comments">
      <div className="write">
        <img src={'/upload/' + currentUser.profilePic} alt="" />
        <input
          type="text"
          value={desc}
          placeholder="write a comment"
          onChange={(e) => setDesc(e.target.value)}></input>
        <button onClick={handleClick}>Add comment</button>
        {isLoading ? (
          <h3>Loading</h3>
        ) : isError ? (
          <h3>Errors</h3>
        ) : (
          data.map((comment) => <Comment comment={comment} />)
        )}
      </div>
    </div>
  );
};
export default Comments;
