import './post.scss';
import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import ShareIcon from '@mui/icons-material/Share';
import { AuthContext } from '../../context/authContext';
import Comments from '../comments/Comments';
import moment from 'moment';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { addRequest } from '../../axios';

const Post = ({ id, userId, profilePic, desc, img, createdAt }) => {
  const { currentUser } = useContext(AuthContext);
  const [liked, setLiked] = useState(false);
  const [commentOpen, setCommentOpen] = useState(false);
  const queryClient = useQueryClient();
  const navigate = useNavigate();

  const { isLoading, isError, data } = useQuery(['likes', id], () => {
    addRequest.get('/likes?postId=' + id).then((res) => {
      return res.data;
    });
  });

  const mutation = useMutation(
    (liked) => {
      if (liked) {
        return addRequest.delete('/likes?postId=' + id);
      } else {
        return addRequest.post('/likes', { postId: id });
      }
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['likes']);
      },
    },
  );

  const handleLike = () => {
    mutation.mutate(data.includes(currentUser.id));
  };

  return (
    <div className="post">
      <div className="container">
        <div className="user">
          <div className="userInfo">
            <img src={profilePic} alt="" />
            <div className="details">
              <Link to={`/profile/${userId}`} className="links">
                <span className="name">name</span>
                <span className="date">{createdAt}</span>
              </Link>
            </div>
          </div>
        </div>
        <div className="content">
          <p>{desc}</p>
          <img src={'./upload' + img} alt="не открылось" />
        </div>
        <div className="info">
          <div className="item">
            <FavoriteBorderIcon
              onClick={handleLike}
              className={data?.includes(currentUser.id) ? 'active' : ''}
            />
            <span>{data?.length} likes</span>
          </div>
          <div onClick={() => setCommentOpen(!commentOpen)} className="item">
            <ChatBubbleOutlineIcon />
            Коментарии
          </div>
          <div className="item">
            <ShareIcon />
            Поделиться
          </div>
        </div>
        <div className="comments">{commentOpen && <Comments postId={id} />}</div>
      </div>
    </div>
  );
};
export default Post;
